import React, { Component } from "react";
import ReactTable from "react-table-6";
import { Redirect } from "react-router-dom";

class Files extends Component {
  state = {
    files: [],
    logout: false,
  };
  signout = () => {
    localStorage.clear();
    this.setState({ logout: true });
  };
  componentDidMount() {
