import React, { Component } from "react";
import ReactTable from "react-table-6";
import { Redirect } from "react-router-dom";

class Files extends Component {
  state = {
    files: [],
    logout: false,
  };
  signout = () => {
    localStorage.clear();
    this.setState({ logout: true });
  };
  componentDidMount() {}
  refresh = () => {
    const url = "http://localhost:5000/showfiles";
    fetch(url, {
      headers: {
        Authorization: "JWT " + localStorage.token,
        // "Content-Type": "application/json",
        // Accept: "application/json",
      },
      method: "GET",
    })
      .then((response) => response.json())
      .then((files) => {
        console.log(files);
        this.setState({ files: JSON.parse(files) });
      });
  };

  render() {
    const columns = [
      {
        Header: "0",
        accesssor: "0",
      },
      {
        Header: "1",
        accesssor: "1",
      },
      {
        Header: "2",
        accesssor: "2",
      },
    ];

    return (
      <div className="container">
        <button className="btn btn-success mx-1" onClick={this.refresh}>
          Refresh
        </button>
        <button className="btn btn-success mx-1" onClick={() => this.signout()}>
          Signout
        </button>
        <ReactTable columns={columns} data={this.state.files}></ReactTable>
        {this.state.logout && <Redirect to="/login" />}
      </div>
    );
  }
}

export default Files;
