import { Route, Redirect } from "react-router-dom";
import React from "react";
export const UserRoute = ({ children, ...rest }) => {
  let token = getToken();

  return (
    <Route
      {...rest}
      render={({ location }) => {
        return token && role === "/" ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: "/login",
              state: { from: location },
            }}
          />
        );
      }}
    />
  );
};

export const GuestRoute = ({ children, ...rest }) => {
  let token = getToken();
  return (
    <Route
      {...rest}
      render={({ location }) => {
        return token ? (
          <Redirect
            to={{
              pathname: "/",
              state: { from: location },
            }}
          />
        ) : (
          children
        );
      }}
    />
  );
};
export const AdminRoute = ({ children, ...rest }) => {
  let token = getToken();
  let role = getRole();
  let path = role === "user" ? "/user" : "/login";
  return (
    <Route
      {...rest}
      render={({ location }) => {
        return token && role === "admin" ? (
          children
        ) : (
          <Redirect
            to={{
              pathname: path,
              state: { from: location },
            }}
          />
        );
      }}
    />
  );
};
export const getToken = () => {
  return localStorage.getItem("token") ? localStorage.getItem("token") : null;
};

export const getRole = () => {
  return localStorage.getItem("role") ? localStorage.getItem("role") : null;
};
