import React, { Component } from "react";
import "./App.css";
import NavBar from "./components/navbar";
import Counters from "./components/counters";
import ReactTable from "react-table-6";
//import "react-table/react-table.css";

class App extends Component {
  state = {
    counters: [
      { id: 1, value: 0 },
      { id: 2, value: 2 },
      { id: 3, value: 3 },
      { id: 4, value: 4 }
    ],
    posts: [],
    dam: []
  };
  componentDidMount() {
    const url = "https://jsonplaceholder.typicode.com/todos";
    fetch(url, {
      method: "GET"
    })
      .then(response => response.json())
      .then(posts => {
        // console.log(posts);
        this.setState({ posts: posts });
      });

    const url2 = "http://localhost:5000/tr_results";
    fetch(url2, {
      method: "GET"
    })
      .then(response => response.json())
      .then(dam => {
        console.log(JSON.parse(dam));
        this.setState({ dam: JSON.parse(dam)[0] });
        console.log(this.state.dam);
      });
  }
  handleReset = () => {
    const counters = this.state.counters.map(c => {
      c.value = 0;
      return c;
    });
    this.setState({ counters });
  };
  handleIncrement = counter => {
    console.log(counter);
    const counters = [...this.state.counters];

    const index = counters.indexOf(counter);
    counters[index] = { ...counter };
    counters[index].value++;
    this.setState({ counters });
  };
  handleDelete = counterId => {
    const counters = this.state.counters.filter(c => c.id !== counterId);
    this.setState({ counters });
  };

  render() {
    const columns = [
      {
        Header: "User Id",
        accessor: "userId"
      },
      {
        Header: "Id",
        accessor: "id"
      },
      {
        Header: "Title",
        accessor: "title"
      },
      {
        Header: "Completed",
        accessor: "completed"
      }
    ];
    return (
      <React.Fragment>
        <NavBar
          totalCounters={this.state.counters.filter(c => c.value !== 0).length}
        />
        <main className="container">
          <Counters
            counters={this.state.counters}
            onDelete={this.handleDelete}
            onReset={this.handleReset}
            onIncrement={this.handleIncrement}
          ></Counters>
        </main>
        {/* <ReactTable columns={columns} data={this.state.posts}></ReactTable> */}
        <ul>
          {this.state.dam.map(obj => {
            return (
              <li>
                {obj.map(item => {
                  return <span>{item} </span>;
                })}
              </li>
            );
          })}
        </ul>
      </React.Fragment>
    );
  }
}

export default App;
