import React, { Component } from "react";
import "./App.css";
import NavBar from "./components/navbar";
import Counters from "./components/counters";
import ReactTable from "react-table-6";
//import "react-table/react-table.css";

class App extends Component {
  state = {
    posts: [],
    dam: [],
    imgFile: null,
  };
  componentDidMount() {}
  fileHandler = (event) => {
    this.setState({ imgFile: event.target.files[0] });
    console.log(this.state);
  };
  fileUpload = () => {
    console.log(this.state.imgFile);
    console.log(this.state);
    const fd = new FormData();
    fd.append("Image", this.state.imgFile, "testtt1.pdf");
    const url = "http://localhost:5000/uploads";
    fetch(url, {
      method: "POST",
      body: fd,
    })
      .then((response) => response.json())
      .then((re) => {
        console.log(re);
      });
  };
  fileDigitize = () => {
    const url = "http://localhost:5000/tr";
    fetch(url, {
      method: "POST",
    })
      .then((response) => response.json())
      .then((dam) => {
        this.setState({ dam: JSON.parse(dam) });
      });
  };

  render() {
    const columns = [
      {
        Header: "0",
        accessor: "0",
      },
      {
        Header: "1",
        accessor: "1",
      },
      {
        Header: "2",
        accessor: "2",
      },
      {
        Header: "3",
        accessor: "3",
      },
      {
        Header: "4",
        accessor: "4",
      },
      {
        Header: "5",
        accessor: "5",
      },
      {
        Header: "6",
        accessor: "6",
      },
    ];
    return (
      <React.Fragment>
        <NavBar />
        <main className="container">
          <input type="file" onChange={this.fileHandler}></input>
          <button onClick={this.fileUpload}>Upload</button>
          <button onClick={this.fileDigitize}>Digitize</button>
          <ReactTable columns={columns} data={this.state.dam}></ReactTable>
        </main>
      </React.Fragment>
    );
  }
}

export default App;
